import React, { Component } from 'react';
import './index.css'
import glasses from './assets/glasses.png'
import exologo from './assets/exologo.png'

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useRouteMatch,
    Redirect,
    useParams,
    withRouter
} from 'react-router-dom';

function MarketPlace() {
    return (
        <Router>
            <div className="header" >
                <img src={exologo} alt="" />
                <AuthButton />

                <ul>
                    <li className="TopBar-1" id="TB1">
                        <Link className="Link-1" to="/home">Home</Link>
                    </li>
                    <li className="TopBar-1">
                        <Link className="Link-1" to="/product">Product</Link>
                    </li>
                    <li className="TopBar-1">
                        <Link className="Link-1" to="/cart">Cart</Link>
                    </li>
                </ul>


                <Switch>
                    <Route exact path="/home" component={Home} />
                    <Route path="/login" component={Login} />
                    <PrivateRoute path="/product" component={Product} />
                    <PrivateRoute path="/cart" component={Cart} />
                </Switch>
            </div>
        </Router>
    )
}

function Cart() {
    
}

const fakeAuth = {
    isAuthenticated: false,
    authenticate(cb) {
        fakeAuth.isAuthenticated = true;
        setTimeout(cb, 100);
    },
    signout(cb) {
        fakeAuth.isAuthenticated = false;
        setTimeout(cb, 100);
    }
}

const AuthButton = withRouter(({ history }) =>
    fakeAuth.isAuthenticated ? (
        <p id="logout">
            <button
                onClick={() => {
                    fakeAuth.signout(() => history.push("/home"));
                }}>
                <p>Sign out</p>
            </button>
        </p>
    ) : (
            <p id="warning-log" >
                You are not Log in!
            </p>
        )
);

function PrivateRoute({ component: Component, ...rest }) {
    return (
        <Route
            {...rest}
            render={props =>
                fakeAuth.isAuthenticated ? (
                    <Component {...props} />
                ) : (
                        <Redirect
                            to={{
                                pathname: "/login",
                                state: { from: props.location }
                            }}
                        />
                    )
            }
        />
    );
}

function Home() {
    return (
        <div className="Home">
        <header class="masthead">
            <div class="container">
                <div class="masthead-subheading">Welcome To Our Store!</div>
                <div class="masthead-heading text-uppercase">It's Nice To Meet You</div>
            </div>
        </header>
        </div>
    )
}

function Product() {

    let { path, url } = useRouteMatch();

    return (
        <div className="header-2">
            {/* <h2>Data Barang</h2> */}
            <ul>
                <li className="TopBar-2">
                    <Link to={`${url}/Albums`}>Albums</Link>
                </li>
                <li className="TopBar-2">
                    <Link to={`${url}/Lightstick`}>Lightstick</Link>
                </li>
            </ul>

            <Switch>
                <Route exact path={path}>

                </Route>
                <Route path={`${path}/:dataId`}>
                    <DataBody />
                </Route>
            </Switch>
        </div>
    )
}

function DataBody() {
    let { path } = useRouteMatch();
  const dispatch = useDispatch();
  const listProduct = useSelector((state) => state.listProduct);

  const _renderCardProduct = (product) => {
    const { img, nama, harga, desc, id, category } = product;
    return (
      <div
        className="col-lg-4 col-md-6 mb-4"
        onClick={() => {
          alert("Product Telah di Tambahkan");
          dispatch(addProductToChart(product));
        }}
      >
        <div className="card h-100">
          <img src={lightBlue} alt="gambar"></img>
          <div className="card-body">
            <h4 className="card-title">
              <a>{nama}</a>
            </h4>
            <h5>Rp {harga}</h5>
            <p className="card-text">
              {desc} - {category}
            </p>
          </div>
          <div className="card-footer">
            <small className="text-muted">
              &#9733; &#9733; &#9733; &#9733; &#9734;
            </small>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-lg-3">
          <h1 className="my-4">List Product</h1>
          <div className="list-group">
            <ul>
              <li className="list-group-item">
                <Link to={`/product/NSG`}>Natural Short Glossy</Link>
              </li>
              <li className="list-group-item">
                <Link to={`/product/OSM`}>Oval Short Matte</Link>
              </li>
            </ul>
          </div>
        </div>
        <div className="col-lg-9">
          <Switch>
            <Route exact path={`/product/NSG`}>
              <br />
              <div className="container">
                <div className="row">
                  {listProduct
                    .filter(
                      (product) => product.category === "Natural Short Glossy"
                    )
                    .map((param) => {
                      return _renderCardProduct(param);
                    })}
                </div>
              </div>
            </Route>
            <Route exact path={`/product/OSM`}>
              <br />
              <div className="container">
                <div className="row">
                  {listProduct
                    .filter(
                      (product) => product.category === "Oval Short Matte"
                    )
                    .map(_renderCardProduct)}
                </div>
              </div>
            </Route>
            <Route exact path={path}></Route>
            <Route exact path={path}></Route>
          </Switch>
        </div>
      </div>
    </div>
  );
}

class Login extends Component {
    state = { redirectToReferrer: false };

    login = () => {
        fakeAuth.authenticate(() => {
            this.setState({ redirectToReferrer: true });
        });
    };

    render() {
        const { from } = this.props.location.state || { from: { pathname: "/home" } };
        const { redirectToReferrer } = this.state;

        if (redirectToReferrer) {
            return <Redirect to={from} />
        }

        return (
            <div className="Login">
              <header class="masthead">
                <div class="container">
                <div class="masthead-subheading">You must log in to view the page</div>
                <button onClick={this.login}>Login</button>
            </div>
            </header>
        </div>
        );
    }
}

export default MarketPlace;
